import React from 'react'
import CONTACTS from '../data/clients1.json'
import Contact from './Contact'

class FilterableProductTable extends React.Component{
  constructor(props){
    console.log(CONTACTS)
    super(props)
    this.state={displayedContacts:CONTACTS}
  }

  handleSearch=(event)=>{
    var searchQuery=event.target.value.toLowerCase();
    var displayedContacts=CONTACTS.filter(function(item ,clientsInfo ,index){
      console.log(index)
      var searchValue = item.name.toLowerCase();
      return searchValue.indexOf(searchQuery) !== -1;
    })

    this.setState({
      displayedContacts:displayedContacts
    });
  }
  render(){
    return (
      <div className="contacts">
        <input type="text" className="search-field" onChange={this.handleSearch}/>
        <ul className="contact-list">
          {
            this.state.displayedContacts.map(function(item ,clientsInfo ,index){
              return <Contact 
                list={item} 
                key = {clientsInfo} />
            })
          }
        </ul>
      </div>

    )

  }

}

export default FilterableProductTable
