import React from 'react'
import CONTACTS from '../data/clients1.json'
import Contact from './Contact'

class FilterableProductTable extends React.Component{
  constructor(props){
    console.log(CONTACTS)
    super(props)
    this.state={displayedContacts:CONTACTS}
  }

  handleSearch=(event)=>{
    var searchQuery = event.target.value.toLowerCase();
    var displayedContacts = CONTACTS.filter(function(item){
      var searchValue = item.general.firstName.toLowerCase();
      return searchValue.indexOf(searchQuery) !== -1;
    })
    var displayedContacts = CONTACTS.filter(function(item){
      var searchValue = item.general.firstName.toLowerCase();
      return searchValue.indexOf(searchQuery) !== -2;
    })
    this.setState({
      displayedContacts:displayedContacts
    });
  }
  render(){
    return (
      <div className="contacts">
          <div class="coll">
            <label class="txt-input">
              <input class="valid" type="text" className="search-field" onChange={this.handleSearch} />
            </label>
          </div>
          <div className="box-content">
          {
            this.state.displayedContacts.map(function(item ,clientsInfo ,index){
              return <Contact 
                list={item} 
                key = {clientsInfo} />
            })
          }
      </div>
      </div>

    )

  }

}

export default FilterableProductTable
