import React from 'react'
import ListDetails from './ListDetails'
import ListData from '../data/clients1.json'


class AppList extends React.Component {
	constructor(props){
    super(props)
    this.state={displayedContacts:ListData}
  }

  handleSearch=(event)=>{
    var searchQuery = event.target.value.toLowerCase();

    var displayedContacts = ListData.filter(function(item){
    	var data = JSON.stingify(item)
    	var searchValue = item.general.firstName.toLowerCase() + 
    	item.general.lastName.toLowerCase() + 
    	item.contact.phone.toLowerCase() + 
    	item.contact.email.toLowerCase() +
    	console.log(searchValue)
      return  searchValue.indexOf(searchQuery) !== -1
  	})
    this.setState({
      displayedContacts:displayedContacts
    });
  }

	hendleDataCallback = (Msg) => {
		var data = Msg.list
		this.props.dataCallBackDetais(data)
	}

	render() {
		return(
			<div className="contacts">
	      <div class="coll">
          <label class="txt-input">
            <input class="valid" type="text" className="search-field" onChange={this.handleSearch} />
          </label>
	      </div>
				<div className="box-content">
					{
						this.state.displayedContacts.map((item ,clientsInfo ,index)=>{
						return <ListDetails 
							list={item} 
							key = {clientsInfo} 
							dataCallback={this.hendleDataCallback}/>
					})}
				</div>
			</div>
		)
	}
}

export default AppList
