import React from 'react'
import ProductRow from './ProductTable'

class ProductTable extends React.Component { 
	render() { 
		// Cоздаем переменные rows и lastCategory 
		let rows = []; 



		this.props.products.forEach((product) => { 
			// Проверяем записи о продуктах на соотвествие фильтру из поиска и наличию товара 
			if (product.name.indexOf(this.props.filterText) === -1 || (!product.stocked && this.props.inStockOnly)) { 
			// При соотвествии условий ничего не отправляем 
			return; 
			// Сортируем товары по категориям и добавляем заголовки категорий 
			} else if (product.category !== lastCategory) { 
			rows.push(<ProductCategoryRow category={product.category} key={product.category} />); 
			} 
			// Добавляем товары в виде компонентов в массив rows 
			rows.push(<ProductRow product={product} key={product.name} />); 
			// Записываем в переменную lastCategory текущую категорию товара 
			lastCategory = product.category; 

			});
		// Добавляем товары в виде компонентов в массив rows 
		rows.push(<ProductRow product={product} key={product.name} />); 
		return ( 
			<table> 
				<tbody> { rows } </tbody> 
			</table> 
		); 
	} 
}
export default ProductTable
