import React from 'react'
import {render} from 'react-dom'
import ListDetails from './list/ListDetails'
import Details from './details/AppDetails'
import ListData from './data/clients1.json'


class App extends React.Component {
	infoDetale(txtMsg){
		alert(txtMsg)
		console.log(txtMsg)
	}
	render() {
		return(
			<div className="box-content">
				{
					ListData.map((item ,clientsInfo ,index)=>{
					return <ListDetails list={item} key = {clientsInfo} infoBack={this.infoDetale}/>
				})}
			</div>

		)
	}
}
const app = document.getElementById("list")
render(<App/>, app);


class AppDetails extends React.Component {
	render() {
		return(
			<Details />
		)
	}
}
const details = document.getElementById("details")
render(<AppDetails />, details);
